{
  "paper_metadata": {
    "title": "Field-specific potassium and phosphorus balances and fertilizer requirements for irrigated rice-based cropping systems",
    "authors": [
      "Roland J Buresh",
      "Mirasol F Pampolino",
      "Christian Witt"
    ],
    "journal": "Plant Soil",
    "publication_year": 2010,
    "doi": "Not specified in text, but published in Plant Soil 2010.335:35-64",
    "study_region": "Asia (tropical and subtropical environments)",
    "primary_focus": "Determination of field-specific K and P fertilizer requirements for irrigated rice-based cropping systems using SSNM, QUEFTS model, and nutrient balances."
  },
  "research_methodology": {
    "study_type": "field_experiment, laboratory_study, modeling_study",
    "experimental_design": "The study utilized a large dataset compiled from various research projects, including on-farm nutrient omission trials, on-farm fertilizer trials, and on-station experiments. Data were grouped by nutrient (N, P, K) and filtered to exclude plots with harvest index (HI) < 0.4 and nutrient omission plots for QUEFTS analysis. Treatments included full fertilization (NPK) and omission plots (-N, -P, -K).",
    "temporal_scope": {
      "study_duration_years": 17,
      "data_collection_frequency": "Various, depending on original experiments (e.g., annual for some long-term experiments)",
      "seasonal_coverage": "Not explicitly stated, but implied to cover multiple cropping seasons (e.g., wet and dry seasons for rice)",
      "monitoring_timeline": [
        "1991-2007 (for on-station experiments in Philippines)",
        "1997-2004 (for on-farm fertilizer trials in Philippines)",
        "1997-2003 (for on-farm fertilizer trials in Vietnam)",
        "1998-2002 (for on-farm fertilizer trials in Indonesia)",
        "2000-2002 (for on-farm trials in Bangladesh and China)",
        "2001-2004 (for on-farm nutrient omission trials in Philippines)",
        "2001-2002 (for on-farm nutrient omission trials in India and Vietnam)",
        "2001-2003 (for on-station experiments in Philippines)"
      ]
    },
    "spatial_scope": {
      "number_of_sites": 7,
      "geographic_coverage": "Seven countries across tropical and subtropical Asia: Bangladesh, China, India, Indonesia, Philippines, Thailand, Vietnam.",
      "site_characteristics": [
        "Irrigated rice production areas",
        "Water rarely limited plant growth",
        "Semi-dwarf, modern high-yielding indica cultivars grown with good agronomic practices"
      ],
      "coordinates": "Not provided for specific sites.",
      "regional_representativeness": "Data from diverse irrigated rice-growing environments and cultivars across Asia."
    },
    "sample_design": {
      "sample_sizes": {
        "QUEFTS_analysis_NPK_fertilized_HI_0.4-0.63": {
          "N": 13327,
          "P": 8404,
          "K": 8521
        },
        "QUEFTS_analysis_all_data": {
          "N": 15507,
          "P": 8948,
          "K": 8983
        },
        "K_omission_plot_trials": 525,
        "P_omission_plot_trials": 531
      },
      "replication_levels": "Not explicitly stated for all original trials, but 'on-farm trials' and 'on-station experiments' imply replication.",
      "randomization_approach": "Not explicitly stated for all original trials.",
      "controls_and_treatments": [
        "Full fertilization (NPK)",
        "Nutrient omission plots (-N, -P, -K)"
      ],
      "statistical_power": "Not explicitly quantified, but large sample sizes are noted as a strength."
    },
    "analytical_methods": {
      "measurement_protocols": [
        "Plant sampling",
        "Yield determination (grain yield adjusted to 0.14 g water g-1 fresh weight)",
        "Analysis for plant nutrients (above-ground dry matter on oven dry basis)"
      ],
      "instrumentation": "Not specified, but comparable methodologies were used across experiments (Witt et al. 1999).",
      "quality_control": [
        "Exclusion of data with harvest index < 0.4",
        "Exclusion of data from plots with suspected yield loss due to pests/disease or unbalanced fertilizer application for QUEFTS analysis"
      ],
      "detection_limits": {
        "Irrigation_water_P_concentration": "<0.1 mg L-1 (negligible)"
      },
      "analytical_precision": {}
    },
    "statistical_approach": {
      "analysis_methods": [
        "QUEFTS (Quantitative Evaluation of the Fertility of Tropical Soils) model (Janssen et al. 1990, Witt et al. 1999)",
        "Linear-parabolic-plateau model for yield-nutrient accumulation relationships",
        "Mean, median, standard deviation, and quartiles for RIE and Harvest Index data",
        "Nutrient balance equations (Equations 1-6)",
        "Fertilizer requirement algorithms (Equations 7-18, 19-24)"
      ],
      "significance_testing": "Implied by discussion of 'significant differences' and 'statistically significant' in other papers within the document.",
      "uncertainty_quantification": "Standard deviations and quartiles provided for RIE and HI. Discussion of uncertainty in K input from irrigation water.",
      "model_validation": "QUEFTS model predictions compared with observed data and previous studies (Witt et al. 1999, Haefele et al. 2003)."
    }
  },
  "quantitative_findings": {
