      {
        "parameter": "Soil K pool in biotite and hydrobiotite (0-40 cm depth) at end of 30-year experiment",
        "values": [
          8000
        ],
        "units": "kg K ha-1",
        "statistical_measures": {},
        "measurement_context": "Loamy sand soil in Scotland"
      },
      {
        "parameter": "Total K in rooting zone (0-40 cm) at end of 30-year experiment",
        "values": [
          72000
        ],
        "units": "kg K ha-1",
        "statistical_measures": {},
        "measurement_context": "Loamy sand soil in Scotland"
      }
    ],
